import 'package:masjid_noor_customer/mgr/models/product_md.dart';class CartMd {  final ProductMd product;  int quantity;  CartMd(      {required this.product,      required this.quantity}); // Convert CartMd to JSON  Map<String, dynamic> toJson() {    return {      'product': product.toJson(),      'quantity': quantity,    };  } // Convert JSON to CartMd  factory CartMd.fromJson(Map<String, dynamic> json) {    return CartMd(      product: ProductMd.fromJson(json['product']),      quantity: json['quantity'],    );  }  double get totalPrice => product.sellPrice * quantity;}