import 'package:equatable/equatable.dart';import 'package:flutter/material.dart';@immutableclass DiscountMd extends Equatable {  final int discountId;  final int productId;  final double discount;  final DateTime startDate;  final DateTime endDate;  const DiscountMd({    required this.discountId,    required this.productId,    required this.discount,    required this.startDate,    required this.endDate,  });  @override  List<Object?> get props => [        discountId,        productId,        discount,        startDate,      ];  factory DiscountMd.fromJson(Map<String, dynamic> json) {    try {      return DiscountMd(        discountId: json['discount_id'],        productId: json['product_id'],        discount: json['discount'],        startDate: DateTime.parse(json['start_date']),        endDate: DateTime.parse(json['end_date']),      );    } on TypeError catch (e, st) {      debugPrint("Error: $e, $st");      rethrow;    }  }  Map<String, dynamic> toJson() {    return {      'discount_id': discountId,      'product_id': productId,      'discount': discount,      'start_date': startDate.toIso8601String(),      'end_date': endDate.toIso8601String(),    };  }}