class JamahMd {  final int id;  final String spotName;  final String spotLocation;  final String fajr;  final String? duhr; // Optional if it can be null  final String? asr; // Optional if it can be null  final String? magrib; // Optional if it can be null  final String? isha; // Optional if it can be null  final List<Map<String, String>>? juma; // List of maps with 'name' and 'time'  final String updatedAt;  JamahMd({    required this.id,    required this.spotName,    required this.spotLocation,    required this.fajr,    this.duhr,    this.asr,    this.magrib,    this.isha,    this.juma,    required this.updatedAt,  });  factory JamahMd.fromJson(Map<String, dynamic> json) {    var jumaFromJson = json['juma'];    List<Map<String, String>> jumaList = jumaFromJson != null        ? List<Map<String, String>>.from(            jumaFromJson.map((j) => Map<String, String>.from(j)))        : [];    return JamahMd(      id: json['id'],      spotName: json['spot_name'],      spotLocation: json['spot_location'],      fajr: json['fajr'],      duhr: json['duhr'],      asr: json['asr'],      magrib: json['magrib'],      isha: json['isha'],      juma: jumaList,      updatedAt: json['updated_at'],    );  }  Map<String, dynamic> toJson() {    return {      'id': id,      'spot_name': spotName,      'spot_location': spotLocation,      'fajr': fajr,      'duhr': duhr,      'asr': asr,      'magrib': magrib,      'isha': isha,      'juma': juma,      'updated_at': updatedAt,    };  }}