import 'package:flutter/material.dart';import 'package:masjid_noor_customer/presentation/utills/extensions.dart';class JamahMd {  final int id;  String spotName;  final String spotLocation;  final TimeOfDay fajr;  final TimeOfDay duhr;  final TimeOfDay asr;  final TimeOfDay magrib;  final TimeOfDay isha;  final List<Map<String, dynamic>>? otherJamah;  final String updatedAt;  JamahMd({    required this.id,    required this.spotName,    required this.spotLocation,    required this.fajr,    required this.duhr,    required this.asr,    required this.magrib,    required this.isha,    required this.otherJamah,    required this.updatedAt,  });  factory JamahMd.fromJson(Map<String, dynamic> json) {    return JamahMd(      id: json['id'],      spotName: json['spot_name'],      spotLocation: json['spot_location'],      fajr: _parseTimeOfDay(json['fajr']),      duhr: _parseTimeOfDay(json['duhr']),      asr: _parseTimeOfDay(json['asr']),      magrib: _parseTimeOfDay(json['magrib']),      isha: _parseTimeOfDay(json['isha']),      otherJamah: (json['other_jamah'] as List?)              ?.map<Map<String, dynamic>>((e) => e as Map<String, dynamic>)              .toList() ??          [],      updatedAt: json['updated_at'],    );  }  Map<String, dynamic> toJson() {    return {      'id': id,      'spot_name': spotName,      'spot_location': spotLocation,      'fajr': fajr.format24Hour(),      'duhr': duhr.format24Hour(),      'asr': asr.format24Hour(),      'magrib': magrib.format24Hour(),      'isha': isha.format24Hour(),      'other_jamah': otherJamah,      'updated_at': updatedAt,    };  }  JamahMd copyWith({    int? id,    String? spotName,    String? spotLocation,    TimeOfDay? fajr,    TimeOfDay? duhr,    TimeOfDay? asr,    TimeOfDay? magrib,    TimeOfDay? isha,    List<Map<String, dynamic>>? otherJamah,    String? updatedAt,  }) {    return JamahMd(      id: id ?? this.id,      spotName: spotName ?? this.spotName,      spotLocation: spotLocation ?? this.spotLocation,      fajr: fajr ?? this.fajr,      duhr: duhr ?? this.duhr,      asr: asr ?? this.asr,      magrib: magrib ?? this.magrib,      isha: isha ?? this.isha,      otherJamah: otherJamah ?? this.otherJamah,      updatedAt: updatedAt ?? this.updatedAt,    );  }  static TimeOfDay _parseTimeOfDay(String time) {    final parts = time.split(':');    return TimeOfDay(hour: int.parse(parts[0]), minute: int.parse(parts[1]));  }}