import 'package:equatable/equatable.dart';class OrderItemMd extends Equatable {  final int orderItemId;  final int orderId;  final String productId;  final int qty;  final double price;  final String name; // Updated to final  final String productImage; // Updated to final  OrderItemMd({    required this.orderItemId,    required this.orderId,    required this.productId,    required this.qty,    required this.price,    required this.name,    required this.productImage,  });  @override  List<Object?> get props => [        orderItemId,        orderId,        productId,        qty,        price,        name,        productImage,      ];  factory OrderItemMd.fromJson(Map<String, dynamic> json) {    return OrderItemMd(      orderItemId: json['order_item_id'] as int,      orderId: json['order_id'] as int,      productId: json['product_id'] as String,      qty: json['qty'] as int,      price: (json['price'] as num).toDouble(),      name: json['product_name'] as String,      productImage: json['product_image'] as String,    );  }  Map<String, dynamic> toJson() {    return {      'order_item_id': orderItemId,      'order_id': orderId,      'product_id': productId,      'qty': qty,      'price': price,      'product_name': name,      'product_image': productImage,    };  }}