import 'package:equatable/equatable.dart';import 'package:flutter/material.dart';@immutableclass ReviewMd extends Equatable {  final int productId;  final int categoryId;  final String name;  final String? description;  final double? stockQty;  final List<String>? images;  final double salePrice;  final double? purchasePrice;  final String? barcode;  const ReviewMd({    required this.productId,    required this.categoryId,    required this.name,    this.description,    this.stockQty,    this.images,    required this.salePrice,    this.purchasePrice,    this.barcode,  });  @override  List<Object?> get props => [        productId,        categoryId,        name,        description,        stockQty,        images,        salePrice,        purchasePrice,        barcode,      ];  factory ReviewMd.fromJson(Map<String, dynamic> json) {    try {      return ReviewMd(        productId: json['product_id'],        categoryId: json['category_id'],        name: json['product_name'],        description: json['description'],        stockQty: json['stock_qty'],        images: json['images'],        salePrice: json['sale_price'],        purchasePrice: json['purchase_price'],        barcode: json['barcode'],      );    } on TypeError catch (e, st) {      debugPrint("Error: $e, $st");      rethrow;    }  }  Map<String, dynamic> toJson() {    return {      'product_id': productId,      'category_id': categoryId,      'product_name': name,      'description': description,      'stock_qty': stockQty,      'images': images,      'sale_price': salePrice,      'purchase_price': purchasePrice,      'barcode': barcode,    };  }}