import 'package:hive/hive.dart';part 'user_md.g.dart';@HiveType(typeId: 0)class UserMd extends HiveObject {  @HiveField(0)  String? userId;  @HiveField(1)  String email;  @HiveField(2)  String? passwordHash;  @HiveField(3)  String? firstName;  @HiveField(4)  String? lastName;  @HiveField(5)  bool? isAdmin;  @HiveField(6)  DateTime? createdAt;  @HiveField(7)  String phoneNumber;  @HiveField(8)  String? username;  @HiveField(9)  String? profilePic;  @HiveField(10)  bool? isDeleted;  UserMd({    this.userId,    required this.email,    this.passwordHash,    this.firstName,    this.lastName,    this.isAdmin,    this.createdAt,    required this.phoneNumber,    this.username,    this.profilePic,    this.isDeleted,  });  factory UserMd.fromJson(Map<String, dynamic> json) {    return UserMd(      userId: json['user_id'] ?? '',      email: json['email'] ?? '',      passwordHash: json['password_hash'] ?? '',      firstName: json['first_name'] ?? '',      lastName: json['last_name'] ?? '',      isAdmin: json['is_admin'] ?? false,      createdAt: DateTime.parse(json['created_at']),      phoneNumber: json['phone_number'] ?? '',      username: json['username'] ?? '',      profilePic: json['profile_pic'] ?? '',      isDeleted: json['is_deleted'] ?? false,    );  }  Map<String, dynamic> toJson() {    return {      'user_id': userId,      'email': email,      'password_hash': passwordHash,      'first_name': firstName,      'last_name': lastName,      'is_admin': isAdmin,      'created_at': createdAt?.toIso8601String(),      'phone_number': phoneNumber,      'username': username,      'profile_pic': profilePic,      'is_deleted': isDeleted,    };  }}