import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:go_router/go_router.dart';class ErrorBoundary extends StatefulWidget {  final Widget child;  const ErrorBoundary({Key? key, required this.child}) : super(key: key);  @override  _ErrorBoundaryState createState() => _ErrorBoundaryState();}class _ErrorBoundaryState extends State<ErrorBoundary> {  Error? _error;  @override  void initState() {    super.initState();    _error = null;    ErrorWidget.builder = (FlutterErrorDetails errorDetails) {      print('Caught Flutter error: ${errorDetails.exception}');      print('Stack trace: ${errorDetails.stack}');      // You can add additional error reporting here      return ErrorDisplay(error: errorDetails.exception);    };  }  @override  Widget build(BuildContext context) {    if (_error != null) {      return ErrorDisplay(error: _error!);    }    return widget.child;  }  @override  void didChangeDependencies() {    super.didChangeDependencies();    _error = null;  }}class ErrorDisplay extends StatelessWidget {  final dynamic error;  const ErrorDisplay({super.key, required this.error});  @override  Widget build(BuildContext context) {    return Scaffold(      body: Center(        child: Padding(          padding: const EdgeInsets.all(16.0),          child: Column(            mainAxisAlignment: MainAxisAlignment.center,            children: [              const Icon(Icons.error_outline, color: Colors.red, size: 60),              const SizedBox(height: 16),              Text(                'Oops! Something went wrong.',                style: TextStyle(fontSize: 18.sp, fontWeight: FontWeight.bold),              ),              const SizedBox(height: 8),              Text(                error.toString(),                textAlign: TextAlign.center,                style: const TextStyle(fontSize: 14),              ),              const SizedBox(height: 16),              ElevatedButton(                onPressed: () {                  context.go('/');                },                child: const Text('Restart App'),              ),            ],          ),        ),      ),    );  }}