import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:go_router/go_router.dart';import 'package:masjid_noor_customer/navigation/router.dart';class ErrorBoundary extends StatefulWidget {  final Widget child;  const ErrorBoundary({super.key, required this.child});  @override  ErrorBoundaryState createState() => ErrorBoundaryState();}class ErrorBoundaryState extends State<ErrorBoundary> {  Error? _error;  @override  void initState() {    super.initState();    _error = null;    ErrorWidget.builder = (FlutterErrorDetails errorDetails) {      debugPrint('Caught Flutter error: ${errorDetails.exception}');      debugPrint('Stack trace: ${errorDetails.stack}');      // You can add additional error reporting here      return ErrorDisplay(error: errorDetails.exception);    };  }  @override  Widget build(BuildContext context) {    if (_error != null) {      return ErrorDisplay(error: _error!);    }    return widget.child;  }  @override  void didChangeDependencies() {    super.didChangeDependencies();    _error = null;  }}class ErrorDisplay extends StatelessWidget {  final dynamic error;  const ErrorDisplay({super.key, required this.error});  @override  Widget build(BuildContext context) {    return Scaffold(      body: Center(        child: Container(          color: Colors.white,          child: Padding(            padding: const EdgeInsets.all(16.0),            child: Column(              mainAxisAlignment: MainAxisAlignment.center,              children: [                const Icon(Icons.error_outline, color: Colors.red, size: 60),                const SizedBox(height: 16),                Text(                  'Oops! Something went wrong.',                  style:                      TextStyle(fontSize: 10.sp, fontWeight: FontWeight.bold),                ),                const SizedBox(height: 8),                TextButton(                  onPressed: () {                    GoRouter.of(context).go(Routes.home);                  },                  child: const Text('Restart App'),                )              ],            ),          ),        ),      ),    );  }}