import 'package:flutter/material.dart';import 'package:get/get.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:masjid_noor_customer/presentation/pages/prayer/prayer_time_controller.dart';import '../../../mgr/models/jamah_md.dart';class JamahTimesPage extends GetView<PrayerTimesController> {  const JamahTimesPage({super.key});  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        title: const Text('Jamah Times'),      ),      body: Obx(() {        if (controller.jamahs.isEmpty) {          return const Center(child: CircularProgressIndicator());        } else {          return ListView.builder(            itemCount: controller.jamahs.length,            itemBuilder: (context, index) {              JamahMd jamah = controller.jamahs[index];              return Card(                margin: EdgeInsets.all(16.w),                child: Padding(                  padding: EdgeInsets.all(16.w),                  child: Column(                    crossAxisAlignment: CrossAxisAlignment.start,                    children: [                      Text(                        jamah.spotName,                        style: TextStyle(                          fontSize: 20.sp,                          fontWeight: FontWeight.bold,                        ),                      ),                      SizedBox(height: 8.h),                      Text(jamah.spotLocation),                      SizedBox(height: 16.h),                      _buildPrayerTime('Fajr', jamah.fajr, context),                      _buildPrayerTime('Dhuhr', jamah.duhr, context),                      _buildPrayerTime('Asr', jamah.asr, context),                      _buildPrayerTime('Maghrib', jamah.magrib, context),                      _buildPrayerTime('Isha', jamah.isha, context),                      SizedBox(height: 5.h),                      if (jamah.otherJamah != null &&                          jamah.otherJamah!.isNotEmpty)                        for (var j in jamah.otherJamah!)                          _buildOtherJamahTimes(j, context),                    ],                  ),                ),              );            },          );        }      }),    );  }  Widget _buildPrayerTime(      String prayerName, TimeOfDay? time, BuildContext context) {    return Padding(      padding: EdgeInsets.symmetric(vertical: 4.h),      child: Row(        mainAxisAlignment: MainAxisAlignment.spaceBetween,        children: [          Text(prayerName, style: const TextStyle(fontWeight: FontWeight.bold)),          Text(time != null ? time.format(context) : 'N/A'),        ],      ),    );  }  Widget _buildOtherJamahTimes(      Map<String, dynamic> otherPrayer, BuildContext context) {    TimeOfDay? time = TimeOfDay(      hour: int.parse(otherPrayer['time'].split(':')[0]),      minute: int.parse(otherPrayer['time'].split(':')[1]),    );    return Column(      children: [        Row(          mainAxisAlignment: MainAxisAlignment.spaceBetween,          children: [            Text(otherPrayer['name'] ?? '',                style: const TextStyle(fontWeight: FontWeight.bold)),            Text(time.format(context) ?? 'N/A'),          ],        ),        SizedBox(height: 5.h),      ],    );  }}