import 'package:flutter/material.dart';import 'package:masjid_noor_customer/presentation/pages/user/user_controller.dart';class ForgotPasswordPage extends StatelessWidget {  final _formKey = GlobalKey<FormState>();  final _emailController = TextEditingController();  ForgotPasswordPage({super.key});  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        title: const Text('Forgot Password'),      ),      body: Padding(        padding: const EdgeInsets.all(16.0),        child: Form(          key: _formKey,          child: Column(            crossAxisAlignment: CrossAxisAlignment.center,            mainAxisAlignment: MainAxisAlignment.center,            children: [              TextFormField(                controller: _emailController,                decoration: const InputDecoration(labelText: 'Email'),                validator: (value) {                  if (value == null || value.isEmpty) {                    return 'Please enter your email';                  }                  return null;                },              ),              const SizedBox(height: 20),              ElevatedButton(                onPressed: () {                  if (_formKey.currentState?.validate() == true) {                    UserController.to.resetPassword(_emailController.text);                  }                },                child: const Text('Reset Password'),              ),            ],          ),        ),      ),    );  }}