import 'package:flutter/material.dart';import 'package:go_router/go_router.dart';import 'package:masjid_noor_customer/presentation/pages/all_export.dart';import 'package:masjid_noor_customer/presentation/pages/user/user_controller.dart';class SignupPage extends StatelessWidget {  final _formKey = GlobalKey<FormState>();  final _nameController = TextEditingController();  final _emailController = TextEditingController();  final _passwordController = TextEditingController();  final _phoneController = TextEditingController();  SignupPage({super.key});  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        title: const Text('Signup'),      ),      body: Padding(        padding: const EdgeInsets.all(16.0),        child: Form(          key: _formKey,          child: SpacedColumn(            verticalSpace: 20.h,            children: [              TextFormField(                controller: _nameController,                decoration: InputDecoration(labelText: 'Name'),                validator: (value) {                  if (value == null || value.isEmpty) {                    return 'Please enter your name';                  }                  return null;                },              ),              TextFormField(                controller: _emailController,                decoration: InputDecoration(labelText: 'Email'),                validator: (value) {                  if (value == null || value.isEmpty) {                    return 'Please enter your email';                  }                  return null;                },              ),              TextFormField(                controller: _passwordController,                decoration: InputDecoration(labelText: 'Password'),                obscureText: true,                validator: (value) {                  if (value == null || value.isEmpty) {                    return 'Please enter your password';                  }                  return null;                },              ),              TextFormField(                controller: _phoneController,                decoration: InputDecoration(labelText: 'Phone'),                validator: (value) {                  if (value == null || value.isEmpty) {                    return 'Please enter your phone number';                  }                  return null;                },              ),              SizedBox(height: 20),              ElevatedButton(                onPressed: () {                  if (_formKey.currentState?.validate() == true) {                    UserController.to.signup(                      _nameController.text,                      _emailController.text,                      _passwordController.text,                      _phoneController.text,                    );                  }                },                child: Text('Signup'),              ),            ],          ),        ),      ),    );  }}