import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';class CartIconCount extends StatelessWidget {  final int count;  final bool? removeBg;  const CartIconCount({super.key, required this.count, this.removeBg});  @override  Widget build(BuildContext context) {    return Padding(      padding: EdgeInsets.all(0.r),      child: Stack(        clipBehavior: Clip.none,        children: [          Container(            padding: EdgeInsets.all(4.r),            decoration: removeBg == true                ? null                : const BoxDecoration(                    color: Colors.white,                    shape: BoxShape.circle,                    boxShadow: [                      BoxShadow(                        color: Colors.black26,                        blurRadius: 4,                        offset: Offset(2, 2),                      ),                    ],                  ),            child: Icon(              Icons.shopping_cart_outlined,              color: const Color(0xFF464C57),              size: 24.w,            ),          ),          Positioned(            top: -8,            right: -8,            child: count == 0                ? const SizedBox.shrink()                : Container(                    padding: EdgeInsets.all(4.w),                    decoration: const BoxDecoration(                      color: Colors.red,                      shape: BoxShape.circle,                    ),                    constraints: const BoxConstraints(                      minWidth: 16,                      minHeight: 16,                    ),                    child: Text(                      count.toString(),                      style: const TextStyle(                        color: Colors.white,                        fontSize: 10,                      ),                      textAlign: TextAlign.center,                    ),                  ),          ),        ],      ),    );  }}