import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:heroicons/heroicons.dart';class CartIconCount extends StatelessWidget {  final int count;  final bool? removeBg;  final double? size;  const CartIconCount(      {super.key, required this.count, this.removeBg, this.size});  @override  Widget build(BuildContext context) {    return Stack(      clipBehavior: Clip.none,      children: [        Container(          padding: removeBg == true ? null : EdgeInsets.all(4.r),          decoration: removeBg == true              ? null              : const BoxDecoration(                  color: Colors.white,                  shape: BoxShape.circle,                  boxShadow: [                    BoxShadow(                      color: Colors.black26,                      blurRadius: 4,                      offset: Offset(2, 2),                    ),                  ],                ),          child: HeroIcon(            HeroIcons.shoppingCart,            color: removeBg == true ? Colors.black : const Color(0xFF464C57),            size: size ?? 24.w,          ),        ),        Positioned(          top: -8,          right: -8,          child: count == 0              ? const SizedBox.shrink()              : Container(                  padding: EdgeInsets.all(4.w),                  decoration: const BoxDecoration(                    color: Colors.red,                    shape: BoxShape.circle,                  ),                  constraints: const BoxConstraints(                    minWidth: 16,                    minHeight: 16,                  ),                  child: Text(                    count.toString(),                    style: const TextStyle(                      color: Colors.white,                      fontSize: 10,                    ),                    textAlign: TextAlign.center,                  ),                ),        ),      ],    );  }}