import 'package:masjid_noor_customer/presentation/pages/all_export.dart';class CartItem extends StatelessWidget {  final ProductMd product;  VoidCallback onIncrease;  VoidCallback onDecrease;  VoidCallback onRemove;  CartItem({    required this.product,    required this.onIncrease,    required this.onDecrease,    required this.onRemove,  });  @override  Widget build(BuildContext context) {    return Row(      children: [        ClipRRect(          borderRadius: BorderRadius.circular(10),          child: Image.network(product.images?.first,              width: 100,              height: 100,              fit: BoxFit.fill, errorBuilder: (context, error, stackTrace) {            return Container(              width: 100,              height: 100,              decoration: BoxDecoration(                color: Colors.grey[200],              ),              child: const Center(                child: Text('No Image'),              ),            );          }),        ),        const SizedBox(width: 10),        Expanded(          child: SpacedColumn(            crossAxisAlignment: CrossAxisAlignment.start,            verticalSpace: 10.h,            children: [              Text(product.name, style: context.textTheme.labelLarge),              Row(                children: [                  SizedBox(                    child: SpacedRow(                      mainAxisAlignment: MainAxisAlignment.spaceBetween,                      crossAxisAlignment: CrossAxisAlignment.center,                      horizontalSpace: 5.w,                      children: [                        GestureDetector(                            onTap: onIncrease,                            child: Container(                                decoration: BoxDecoration(                                  color: Colors.white,                                  border: Border.all(color: Colors.black38),                                  shape: BoxShape.circle,                                ),                                child: const Icon(Icons.add))),                        SizedBox(                          width: 20,                          child: Center(                              child: Text((product.cartQty ?? 1).toString(),                                  style:                                      context.textTheme.labelMedium?.copyWith(                                    color: Colors.black,                                    fontSize: 18,                                    fontWeight: FontWeight.bold,                                  ))),                        ),                        GestureDetector(                            onTap: onDecrease,                            child: Container(                                decoration: BoxDecoration(                                  color: Colors.white,                                  border: Border.all(color: Colors.black38),                                  shape: BoxShape.circle,                                ),                                child: const Icon(Icons.remove))),                      ],                    ),                  ),                ],              ),              Text("â‚© ${product.sellPrice.toString()}",                  style: context.textTheme.labelMedium),            ],          ),        ),        IconButton(          icon: const Icon(Icons.delete),          onPressed: onRemove,        ),      ],    );  }}