import 'package:flutter/material.dart';class DashboardCard extends StatelessWidget {  final String title;  final String value;  const DashboardCard({    super.key,    required this.title,    required this.value,  });  @override  Widget build(BuildContext context) {    return Container(      decoration: BoxDecoration(        color: const Color(0xFFf2f5fa),        borderRadius: BorderRadius.circular(12.0),        border: Border.all(          color: Colors.grey[200]!,          width: 1.0,        ),      ),      child: Padding(        padding: const EdgeInsets.all(16.0),        child: Column(          crossAxisAlignment: CrossAxisAlignment.start,          children: [            Text(              title,              style: const TextStyle(                fontSize: 18.0,                fontWeight: FontWeight.bold,              ),            ),            const SizedBox(height: 8.0),            Text(              value,              style: TextStyle(                fontSize: 16.0,                color: Colors.grey[700],              ),            ),          ],        ),      ),    );  }}