import 'package:go_router/go_router.dart';import 'package:masjid_noor_customer/presentation/pages/all_export.dart';class Header extends StatelessWidget {  final String name;  const Header({Key? key, required this.name}) : super(key: key);  @override  Widget build(BuildContext context) {    return Container(      padding: EdgeInsets.all(10.r),      child: Row(children: [        if (name.toLowerCase().contains("home"))          Text(name, style: context.textTheme.labelMedium),        if (name.toLowerCase().contains("products"))          Text("Products", style: context.textTheme.labelMedium),      ]),    );  }}