import 'package:flutter/material.dart';class Header extends StatelessWidget {  final String name;  const Header({Key? key, required this.name}) : super(key: key);  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.symmetric(vertical: 10),      child: Container(        padding: const EdgeInsets.symmetric(horizontal: 10.0, vertical: 20.0),        decoration: BoxDecoration(            borderRadius: BorderRadius.circular(10.0),            border: Border.all(color: Colors.grey.shade300)),        child: Row(          children: [            if (MediaQuery.of(context).size.width < 800)              InkWell(                  onTap: () {                    Scaffold.of(context).openDrawer();                  },                  child: const Icon(Icons.menu)),            const SizedBox(width: 10.0),            Text(              name,              style:                  const TextStyle(fontSize: 18.0, fontWeight: FontWeight.bold),            ),          ],        ),      ),    );  }}