import 'package:flutter/material.dart';import 'package:masjid_noor_customer/mgr/models/order_md.dart';import 'package:masjid_noor_customer/presentation/utills/extensions.dart';class OrderStatusWidget extends StatelessWidget {  final OrderStatus status;  const OrderStatusWidget({super.key, required this.status});  @override  Widget build(BuildContext context) {    return Container(      padding: const EdgeInsets.symmetric(horizontal: 8.0, vertical: 4.0),      decoration: BoxDecoration(        color: orderStatusColor(status),        borderRadius: BorderRadius.circular(5.0),      ),      child: Text(        status.toShortString().capitalize(),        textAlign: TextAlign.center,        style: const TextStyle(          fontSize: 12.0,          fontWeight: FontWeight.bold,        ),      ),    );  }  Color orderStatusColor(OrderStatus status) {    switch (status) {      case OrderStatus.pending:        return Colors.yellow.shade300;      case OrderStatus.processing:        return Colors.blue.shade300;      case OrderStatus.completed:        return Colors.green.shade300;      case OrderStatus.cancelled:        return Colors.red.shade300;      default:        return Colors.white;    }  }}