import 'package:masjid_noor_customer/presentation/pages/all_export.dart';class PaginationWidget extends StatelessWidget {  final VoidCallback onNextPage;  final VoidCallback onPrevPage;  final int currentPage;  final int totalPage;  const PaginationWidget({    super.key,    required this.onNextPage,    required this.onPrevPage,    required this.currentPage,    required this.totalPage,  });  @override  Widget build(BuildContext context) {    return Row(      mainAxisSize: MainAxisSize.min,      crossAxisAlignment: CrossAxisAlignment.center,      mainAxisAlignment: MainAxisAlignment.center,      children: [        OutlinedButton.icon(          onPressed: onPrevPage,          style: OutlinedButton.styleFrom(            side: const BorderSide(color: Colors.grey),          ),          icon: const Icon(Icons.chevron_left),          label: const Text('Prev Page'),        ),        const SizedBox(width: 8),        SizedBox(            width: 100,            child: Text('Page $currentPage of $totalPage',                textWidthBasis: TextWidthBasis.longestLine,                overflow: TextOverflow.ellipsis,                textAlign: TextAlign.center,                style: context.textTheme.bodyMedium!)),        const SizedBox(width: 8),        OutlinedButton.icon(          onPressed: onNextPage,          style: OutlinedButton.styleFrom(            side: const BorderSide(color: Colors.grey),          ),          iconAlignment: IconAlignment.end,          icon: const Icon(Icons.chevron_right),          label: const Text('Next Page'),        ),      ],    );  }}