import 'package:flutter/material.dart';import 'package:go_router/go_router.dart';import 'package:masjid_noor_customer/presentation/pages/all_export.dart';import 'package:masjid_noor_customer/presentation/pages/cart/cart_controller.dart';class ProductItem extends StatefulWidget {  final ProductMd product;  final String parentRoute;  const ProductItem({    super.key,    required this.product,    required this.parentRoute,  });  @override  _ProductItemState createState() => _ProductItemState();}class _ProductItemState extends State<ProductItem>    with TickerProviderStateMixin {  bool isTapped = false;  void addToCart(ProductMd product) {    CartController.to.addToCart(product);  }  @override  Widget build(BuildContext context) {    return SizedBox(      width: 170.w,      child: Align(        alignment: Alignment.center,        child: InkWell(          highlightColor: Colors.white,          splashColor: Colors.white,          hoverColor: Colors.white,          onHighlightChanged: (value) {            setState(() {              isTapped = value;            });          },          onTap: () {            context.go('${Routes.productDetails}/${widget.product.id}', extra: {              'id': widget.product.id.toString(),              'parentRoute': widget.parentRoute            });          },          child: AnimatedContainer(            duration: const Duration(milliseconds: 300),            curve: Curves.fastLinearToSlowEaseIn,            child: Column(              crossAxisAlignment: CrossAxisAlignment.start,              children: [                Stack(                  children: [                    ClipRRect(                      borderRadius: BorderRadius.circular(10.r),                      child: Image.network(                        widget.product.images?.first ??                            'https://picsum.photos/100/100',                        height: isTapped ? 130.h : 135.h,                        width: isTapped ? 170.w : 172.w,                        fit: BoxFit.fill,                        errorBuilder: (context, error, stackTrace) {                          return Container(                            height: isTapped ? 130.h : 135.h,                            width: isTapped ? 170.w : 172.w,                            decoration: BoxDecoration(                              color: const Color(0xFFE0E0E0),                              borderRadius: BorderRadius.circular(10.r),                            ),                            child: Center(                              child: Text(                                'No Image',                                style: context.textTheme.labelMedium?.copyWith(                                  color: const Color(0xFF2B3544),                                ),                              ),                            ),                          );                        },                      ),                    ),                    Positioned(                      bottom: 8,                      right: 10,                      child: InkWell(                        onTap: () {                          addToCart(widget.product);                        },                        child: Padding(                          padding: EdgeInsets.all(0.r),                          child: Stack(                            clipBehavior: Clip.none,                            children: [                              Container(                                padding: EdgeInsets.all(4.r),                                decoration: const BoxDecoration(                                  color: Colors.white,                                  shape: BoxShape.circle,                                  boxShadow: [                                    BoxShadow(                                      color: Colors.black26,                                      blurRadius: 4,                                      offset: Offset(2, 2),                                    ),                                  ],                                ),                                child: Icon(                                  Icons.shopping_cart_outlined,                                  color: const Color(0xFF464C57),                                  size: 24.w,                                ),                              ),                              Positioned(                                top: -8,                                right: -8,                                child: Obx(() {                                  final itemCount = CartController.to.cartItems                                      .firstWhereOrNull((item) =>                                          item.product.id == widget.product.id)                                      ?.quantity;                                  if (itemCount == null || itemCount <= 0) {                                    return const SizedBox.shrink();                                  }                                  return Container(                                    padding: EdgeInsets.all(4.w),                                    decoration: const BoxDecoration(                                      color: Colors.red,                                      shape: BoxShape.circle,                                    ),                                    constraints: const BoxConstraints(                                      minWidth: 16,                                      minHeight: 16,                                    ),                                    child: Text(                                      itemCount.toString(),                                      style: const TextStyle(                                        color: Colors.white,                                        fontSize: 10,                                      ),                                      textAlign: TextAlign.center,                                    ),                                  );                                }),                              ),                            ],                          ),                        ),                      ),                    ),                  ],                ),                Padding(                  padding: EdgeInsets.only(left: 5.w, top: 5.h),                  child: SpacedColumn(                    crossAxisAlignment: CrossAxisAlignment.start,                    children: [                      SizedBox(                        width: 248.w,                        child: Text(                          widget.product.name,                          maxLines: 2,                          overflow: TextOverflow.ellipsis,                          style: context.textTheme.labelMedium?.copyWith(                            color: const Color(0xFF2B3544),                          ),                        ),                      ),                      Text(                        "â‚© ${widget.product.sellPrice.toString()}",                        style: context.textTheme.labelMedium?.copyWith(                          color: const Color(0xFF701515),                        ),                      ),                    ],                  ),                ),              ],            ),          ),        ),      ),    );  }}